mapper.select:
SELECT USER_ID, ID, NICKNAME, GENDER, BIRTHDAY, JOIN_DATE, PW_MODIFY_DATE, SESSION_ID, SESSION_ID, SESSION_LIMIT_DATE, BANNED_STATE
  FROM USERS


        ID = #{id}


        AND PW = #{pw}


        AND NICKNAME = #{nickname}

mapper.update:
UPDATE USERS
   SET SESSION_ID = #{sessionId}
     , SESSION_LIMIT_DATE = #{sessionLimitDate}
 WHERE ID = #{id}
